#!/bin/bash
# Diretório de origem e destino ORIGEM="/data/data/com.termux/files/home/WUZAPI-CHAT-BOT/files/" DESTINO="/storage/emulated/0/Tasker/termux/TASKER-WUZAPI/"
# Copia apenas a pasta "user_2" e todo o seu conteúdo cp -r "$ORIGEM/%user" "$DESTINO/"

#!/bin/bash
find /data/data/com.termux/files/home/WUZAPI-CHAT-BOT/files/%user -type f ! -name "*.json" -delete
echo "Arquivos dentro da pasta user_1 foram deletados, exceto os arquivos .json!"

// Lê a variável local Tasker com o JSON, ex.: %users
var jsonString = local('users'); // Obtém a variável local

// Faz o parse do JSON para um objeto JavaScript
var usersArray = JSON.parse(jsonString);

// Array para armazenar as informações extraídas como texto
var resultLines = [];
// Array para armazenar apenas os identificadores user_X
var userIdentifiers = [];

// Itera sobre cada usuário no array
for (var i = 0; i < usersArray.length; i++) {
    var user = usersArray[i];
    // Monta o texto para cada usuário
    var userText = 'user: user_' + (i + 1) + '\n' +
                   'salve_media: ' + user.salve_media + '\n' +
                   'auto_del: ' + user.auto_del;
    resultLines.push(userText);
    // Adiciona o identificador user_X ao array de identificadores
    userIdentifiers.push('user_' + (i + 1));
}

// Junta os grupos de usuários com ------- como separador
var finalText = resultLines.join('\n-------\n');

// Armazena o resultado como texto puro na variável local connected_users
setLocal('connected_users', finalText);

// Armazena os identificadores (user_1,user_2,user_3) em outra variável local, separados por vírgula
setLocal('connected_users_list', userIdentifiers.join(','));

// Armazena a quantidade de usuários encontrados na variável local total_users
setLocal('total_users', usersArray.length.toString());

// Obtém o conteúdo da variável local do Tasker
var user_list = local("user_list");

// Divide a string em linhas
var linhas = user_list.split("\n");

// Inicializa variáveis de saída
var user = "";
var salve_media = "";
var auto_del = "";

// Percorre cada linha para extrair os valores
linhas.forEach(function(linha) {
    var partes = linha.split(": "); // Divide na ocorrência de ": "
    if (partes.length === 2) {
        var chave = partes[0].trim();
        var valor = partes[1].trim();

        // Atribui os valores às variáveis correspondentes
        if (chave === "user") user = valor;
        if (chave === "salve_media") salve_media = valor;
        if (chave === "auto_del") auto_del = valor;
    }
});

// Retorna os valores para o Tasker
setLocal("user", user);
setLocal("salve_media", salve_media);
setLocal("auto_del", auto_del);

var intentData = local("shortcut");

// Extrair JID (funciona para contato ou grupo)
var jidStart = intentData.indexOf("S.jid=");
var numero = "Não encontrado";

if (jidStart >= 0) {
    jidStart += 6; // tamanho de "S.jid="
    var jidEnd = intentData.indexOf(";", jidStart); // termina no ;
    if (jidEnd >= 0) {
        var jid = intentData.substring(jidStart, jidEnd);
        numero = jid.split("%40")[0]; // remove o sufixo (ex: %40s.whatsapp.net ou %40g.us)
    }
}

// Extrair nome (displayname)
var nameStart = intentData.indexOf("S.displayname=");
var nome = "Não encontrado";

if (nameStart >= 0) {
    nameStart += 14; // tamanho de "S.displayname="
    var nameEnd = intentData.indexOf(";", nameStart);
    if (nameEnd >= 0) {
        var nomeCodificado = intentData.substring(nameStart, nameEnd);
        nome = decodeURIComponent(nomeCodificado);
    }
}

// Salvar variáveis locais
setLocal("number", numero);
setLocal("name", nome);

