let connectionStatus = '%status';  // Pode ser alterado para 'sucess' ou 'err'
let jsonData = %last_object;
function checkConnection() {
    const statusText = document.getElementById('statusText');
    const jsonContainer = document.getElementById('jsonData');
    statusText.classList.remove('success', 'error', 'default');
    setTimeout(() => {
        if (connectionStatus === 'sucess') {
            statusText.textContent = 'Conexão bem-sucedida';
            statusText.classList.add('success');
            jsonContainer.textContent = `{ \n${Object.entries(jsonData).map(([key, value]) => `"${key}": "${value}"`).join(', \n')} \n}`;
        } else if (connectionStatus === 'err') {
            statusText.textContent = 'Erro na conexão';
            statusText.classList.add('errr');
            jsonContainer.textContent = '';
        }
    }, 6000); 
    setTimeout(() => {
        if (connectionStatus === 'sucess') {
//alert('Conexão bem-sucedida!');
            tk.destroyScene("CONNECTION_STATUS v(1.0)");
        } else if (connectionStatus === 'err') {
            tk.destroyScene("CONNECTION_STATUS v(1.0)");
//alert('Erro na conexão!'); 
        }
    }, 12000);
}
checkConnection();

