const inputPhone = local('phone');
const inputPort = local('port');

let isPhoneInUse = false;
let isPortInUse = false;

usersArray.forEach((item) => {
    if (item.phone === inputPhone) {
        isPhoneInUse = true;
    }
    if (item.port === inputPort) {
        isPortInUse = true;
    }
});

