if (jsonsInputs) {
    jsonsInputs = JSON.parse(jsonsInputs);

    const phoneToFind = local('phone');

    // Encontrar o item com o número de telefone especificado
    foundItem = jsonsInputs.find(item => item.phone === phoneToFind);

    if (foundItem) {
        // Definir o item encontrado como resultado
        setLocal('check_removed_user', JSON.stringify(foundItem, null, 2));
    } else {
        // Se nenhum item for encontrado
        setLocal('check_removed_user', 'Número de telefone não encontrado.');
    }
} else {
    setLocal('check_removed_user', "Nenhum dado encontrado em 'users'.");
}

