    const initialLength = jsonsInputs.length;

    jsonsInputs = jsonsInputs.filter(item => item.phone !== phoneToRemove);

    if (jsonsInputs.length === initialLength) {
        outputMessage = `Número de telefone ${phoneToRemove} não encontrado.`;
    } else {
        users= JSON.stringify(jsonsInputs, null, 2);
