    setLocal("profile_url", response.data.url);
    setLocal("profile_id", response.data.id);
    setLocal("profile_type", response.data.type);
    setLocal("profile_path", response.data.direct_path);
} else {
    setLocal("error", "Erro ao obter imagem de perfil.");
}

var httpData = local('http_data');

try {
    var jsonData = JSON.parse(httpData);
    var formattedData = [];

    if (jsonData.code !== 200 || !jsonData.data) {
        throw new Error("Invalid JSON structure");
    }

    var amountContacts = Object.keys(jsonData.data).length;

    for (var contact in jsonData.data) {
        var phoneNumber = contact.split('@')[0];
        var contactInfo = jsonData.data[contact];
        
        var name = contactInfo.FullName || contactInfo.FirstName || contactInfo.BusinessName || "";
        var isWhatsapp = name ? true : false;
        
        formattedData.push({
            PhoneNumber: phoneNumber,
            PhoneName: name,
            IsWhatsapp: isWhatsapp,
            FirstName: contactInfo.FirstName || "",
            PushName: contactInfo.PushName || ""
        });
    }
    
    setLocal("json_contacts", JSON.stringify(formattedData, null, 2));
    setLocal("amount_contacts", amountContacts.toString());
} catch (error) {
    setLocal("json_contacts", JSON.stringify({ error: error.message }, null, 2));
    setLocal("amount_contacts", "0");
}

var httpData = local('http_data');

function formatDateToBR(dateString) {
    var date = new Date(dateString);
    var options = {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
        timeZone: 'America/Sao_Paulo',
        hour12: false
    };
    return date.toLocaleString('pt-BR', options);
}

function extractPhoneNumber(jid) {
    return jid.split('@')[0];
}

try {
    var jsonData = JSON.parse(httpData);
    
    var groupData = jsonData.data.Groups.map(function(group) {
        return {
            GroupName: group.Name,
            GroupCreated: formatDateToBR(group.GroupCreated),
            OwnerPhone: extractPhoneNumber(group.OwnerJID),
            Participants: group.Participants.map(function(participant) {
                return {
                    Phone: extractPhoneNumber(participant.JID),
                    IsAdmin: participant.IsAdmin,
                    IsSuperAdmin: participant.IsSuperAdmin
                };
            })
        };
    });
    
    var formattedData = JSON.stringify(groupData, null, 2);
    
    setLocal('json_group', formattedData);
} catch (error) {
    setLocal('json_group', JSON.stringify({ error: error.message }, null, 2));
}

