const inputJSON = local("json_response");

const cleanedJSON = inputJSON.split("&token=")[0];
const tokenMatch = inputJSON.match(/&token=(\d+)/);

try {
    const jsonData = JSON.parse(cleanedJSON);
    const info = jsonData.event.Info;
    const mensagem = jsonData.event.Message;

    setLocal("full_json", inputJSON);

    if (tokenMatch && tokenMatch[1]) {
        setLocal("port", tokenMatch[1]);
    }

    setLocal("sender_number", info.Sender);
    setLocal("my_number", info.IsFromMe);
    setLocal("isgroup", info.IsGroup);
    setLocal("message_id", info.ID);
    setLocal("type", info.Type);
    setLocal("pushname", info.PushName);

    const data = new Date();
    const horarioBR = new Date(data.toLocaleString("en-US", { timeZone: "America/Sao_Paulo" }));
    const formatarNumero = num => ('0' + num).slice(-2);
    const dia = formatarNumero(horarioBR.getDate());
    const mes = formatarNumero(horarioBR.getMonth() + 1);
    const ano = horarioBR.getFullYear();
    const hora = formatarNumero(horarioBR.getHours());
    const minuto = formatarNumero(horarioBR.getMinutes());
    const segundo = formatarNumero(horarioBR.getSeconds());
    setLocal("time_stamp", `${hora}:${minuto}:${segundo} ${dia}/${mes}/${ano}`);

    setLocal("sender_received", info.DeviceSentMeta ? info.DeviceSentMeta.DestinationJID : null);

    if (info.IsGroup) {
        setLocal("jid", info.Chat);
    }

    if (mensagem) {
        setLocal("message", mensagem.conversation || mensagem.extendedTextMessage?.text || "");

        if (mensagem.stickerMessage) {
            setLocal("type", "stick_message");
            setLocal("file_extension", mensagem.stickerMessage.mimetype ? mensagem.stickerMessage.mimetype.split('/')[1] : "");
        }

        if (mensagem.contactMessage && mensagem.contactMessage.vcard) {
            setLocal("type", "phone_contact");
            const vCard = mensagem.contactMessage.vcard;
            const phoneMatch = vCard.match(/item1.TEL;waid=(\d+):(\+[\d\s\(\)-]+)/);
            const numeroTelefone = phoneMatch ? phoneMatch[2].trim() : null;
            setLocal("contact_phone_vcard", JSON.stringify([{
                "nome": mensagem.contactMessage.displayName || "Desconhecido",
                "contato": numeroTelefone
            }]));
        } else if (mensagem.contactsArrayMessage?.contacts?.length > 0) {
            setLocal("type", "phone_contact");
            const contatos = mensagem.contactsArrayMessage.contacts.map(contato => {
                const vCard = contato.vcard;
                const phoneMatch = vCard.match(/item1.TEL;waid=(\d+):(\+[\d\s\(\)-]+)/);
                return {
                    "nome": contato.displayName || "Desconhecido",
                    "contato": phoneMatch ? phoneMatch[2].trim() : null
                };
            });
            setLocal("contact_phone_vcard", JSON.stringify(contatos));
        }

        if (mensagem.audioMessage) {
            setLocal("type", "audio_message");
            let extension = mensagem.audioMessage.mimetype ? mensagem.audioMessage.mimetype.split('/')[1].split(';')[0] : "";
            if (extension === "mpeg") extension = "mp3"; // Mapeia "mpeg" para "mp3"
            if (mensagem.audioMessage.PTT && extension === "ogg") extension = "opus"; // Mapeia "ogg" para "opus" apenas para PTT
            setLocal("file_extension", extension);
        }

        if (mensagem.imageMessage) {
            setLocal("type", "image_message");
            setLocal("%image_caption", mensagem.imageMessage.caption || "");
            setLocal("file_extension", mensagem.imageMessage.mimetype ? mensagem.imageMessage.mimetype.split('/')[1] : "");
        }

        if (mensagem.videoMessage && mensagem.videoMessage.gifPlayback) {
            setLocal("type", "gif_message");
            setLocal("file_extension", "gif");
            setLocal("%image_caption", mensagem.videoMessage.caption || "");
        } else if (mensagem.videoMessage) {
            setLocal("type", "video_message");
            setLocal("file_extension", mensagem.videoMessage.mimetype ? mensagem.videoMessage.mimetype.split('/')[1] : "");
            setLocal("video_caption", mensagem.videoMessage.caption || "");
        }

        if (mensagem.documentMessage) {
            setLocal("type", "document_message");
            let extension = mensagem.documentMessage.mimetype ? mensagem.documentMessage.mimetype.split('/')[1] : "";
            if (extension === "plain") extension = "txt"; // Mapeia "plain" para "txt"
            setLocal("file_extension", extension);
            setLocal("%arq_caption", mensagem.documentMessage.caption || "");
            // Captura o nome do arquivo sem a extensão
            const fileNameFull = mensagem.documentMessage.fileName || "";
            const fileName = fileNameFull.substring(0, fileNameFull.lastIndexOf('.')) || fileNameFull;
            setLocal("file_name", fileName);
        }

        if (mensagem.reactionMessage) {
            setLocal("type", "reaction_message");
            setLocal("reaction_text", mensagem.reactionMessage.text);
        }

        // Ajuste para identificar link_message sempre que for um link
        if (mensagem.conversation?.match(/^https?:\/\//)) {
            setLocal("type", "link_message");
            // Move a definição de text_message para dentro de um else para evitar sobrescrever
        } else if (mensagem.conversation) {
            setLocal("type", "text_message");
        }

        if (mensagem.extendedTextMessage?.matchedText?.match(/^https?:\/\//)) {
            setLocal("type", "link_message");
            setLocal("link_title", mensagem.extendedTextMessage.title || "");
            setLocal("link_description", mensagem.extendedTextMessage.description || "");
        }

        if (mensagem.locationMessage) {
            setLocal("receiver_latitude", mensagem.locationMessage.degreesLatitude);
            setLocal("receiver_longitude", mensagem.locationMessage.degreesLongitude);
            setLocal("%link_location_maps", `https://www.google.com/maps?q=${mensagem.locationMessage.degreesLatitude},${mensagem.locationMessage.degreesLongitude}`);
            setLocal("type", "link_message");
        }

        if (mensagem.messageContextInfo?.messageSecret) {
            setLocal("messagesecret", mensagem.messageContextInfo.messageSecret);
        }

    }

} catch (e) {}

let inputPort = (local('port') || '').toString().trim();
let usersArray = JSON.parse(local('users') || '[]');

let foundUser = usersArray.find(user => {
    return user && user.port && user.port.toString().trim() === inputPort;
});

setLocal('modo_op', foundUser ? foundUser.modo_op : '');

function normalizeAndValidate(jsonString, varName) {
    try {
        if (!jsonString || jsonString.trim() === "") {
            let currentLog = local("error_log") || "";
            let currentVarLog = local(`err_var_${varName}`) || "";
            setLocal("error_log", currentLog + `${varName}: Variável vazia ou inválida.\n`);
            setLocal(`err_var_${varName}`, currentVarLog + `Erro em ${varName}: Variável vazia ou inválida.\n`);
            return { valid: false, data: [], cleanedString: JSON.stringify([], null, 2), hasAnomalies: true };
        }
        let cleaned = jsonString.replace(/\s+/g, ' ').trim();
        let parsed = JSON.parse(cleaned);
        if (!Array.isArray(parsed)) {
            parsed = [parsed];
            let currentLog = local("error_log") || "";
            let currentVarLog = local(`err_var_${varName}`) || "";
            setLocal("error_log", currentLog + `${varName}: Não era um array, convertido para array na linha 0.\n`);
            setLocal(`err_var_${varName}`, currentVarLog + `Erro em ${varName}: Não era um array, convertido para array na linha 0.\n`);
        }
        let corrected = parsed.map((user, index) => {
            let anomalies = [];
            let correctedUser = { ...user };
            if (!correctedUser.phone) {
                anomalies.push(`Telefone ausente na linha ${index + 1}.`);
                correctedUser.phone = "DESCONHECIDO";
            } else {
                let cleanPhone = correctedUser.phone.replace(/[^0-9]/g, '');
                if (cleanPhone.length !== 12) {
                    anomalies.push(`Telefone inválido (${correctedUser.phone}) na linha ${index + 1}. Deve ter exatamente 12 dígitos.`);
                    correctedUser.phone = "DESCONHECIDO";
                } else {
                    correctedUser.phone = cleanPhone.replace(/(\d{2})(\d{2})(\d+)/, '$1 $2 $3');
                }
            }
            let portNum = parseInt(correctedUser.port, 10);
            if (!correctedUser.port || isNaN(portNum) || portNum < 1000 || portNum > 9999 || correctedUser.port.length !== 4) {
                anomalies.push(`Porta inválida (${correctedUser.port}) na linha ${index + 1}. Usando 7774 como padrão.`);
                correctedUser.port = "7774";
            }
            const validModoOp = ["send and receive", "send only", "receive only"];
            if (!validModoOp.includes(correctedUser.modo_op)) {
                anomalies.push(`modo_op inválido (${correctedUser.modo_op}) na linha ${index + 1}. Usando "send and receive".`);
                correctedUser.modo_op = "send and receive";
            }
            const validSalveMedia = ["yes", "no"];
            if (!validSalveMedia.includes(correctedUser.salve_media)) {
                anomalies.push(`salve_media inválido (${correctedUser.salve_media}) na linha ${index + 1}. Usando "yes".`);
                correctedUser.salve_media = "yes";
            }
            const validAutoDel = ["yes", "no"];
            if (!validAutoDel.includes(correctedUser.auto_del)) {
                anomalies.push(`auto_del inválido (${correctedUser.auto_del}) na linha ${index + 1}. Usando "no".`);
                correctedUser.auto_del = "no";
            }
            if (anomalies.length > 0) {
                let currentLog = local("error_log") || "";
                let currentVarLog = local(`err_var_${varName}`) || "";
                setLocal("error_log", currentLog + `${varName}: ${anomalies.join(" | ")}\n`);
                setLocal(`err_var_${varName}`, currentVarLog + `Erro em ${varName}: ${anomalies.join(" | ")}\n`);
            }
            return correctedUser;
        });
        return { valid: true, data: corrected, cleanedString: JSON.stringify(corrected, null, 2), hasAnomalies: corrected.some((_, i) => JSON.stringify(parsed[i]) !== JSON.stringify(corrected[i])) || !Array.isArray(JSON.parse(cleaned)) };
    } catch (e) {
        let currentLog = local("error_log") || "";
        let currentVarLog = local(`err_var_${varName}`) || "";
        setLocal("error_log", currentLog + `${varName}: Erro ao parsear JSON: ${e.message}\n`);
        setLocal(`err_var_${varName}`, currentVarLog + `Erro em ${varName}: Erro ao parsear JSON: ${e.message}\n`);
        return { valid: false, data: [], cleanedString: JSON.stringify([], null, 2), hasAnomalies: true };
    }
}

function processVariables() {
    let usersValue = local("users");
    let usersTxtValue = local("users_txt");
    let usersResult = normalizeAndValidate(usersValue, "users");
    let usersTxtResult = normalizeAndValidate(usersTxtValue, "users_txt");
    setLocal("users_corrected", usersResult.cleanedString);
    setLocal("users_txt_corrected", usersTxtResult.cleanedString);
    let currentLog = local("error_log") || "";
    let errVar = "";
    if (usersResult.hasAnomalies && !usersTxtResult.hasAnomalies) {
        setLocal("error_log", currentLog + "Problema detectado apenas em users.\n");
        errVar = "err_var_users";
    } else if (!usersResult.hasAnomalies && usersTxtResult.hasAnomalies) {
        setLocal("error_log", currentLog + "Problema detectado apenas em users_txt.\n");
        errVar = "err_var_txt";
    } else if (usersResult.hasAnomalies && usersTxtResult.hasAnomalies) {
        setLocal("error_log", currentLog + "Problemas detectados em ambas as variáveis (users e users_txt).\n");
        errVar = "err_var_users,err_var_txt";
    }
    setLocal("err_local", errVar);
    if (JSON.stringify(usersResult.data) !== JSON.stringify(usersTxtResult.data)) {
        setLocal("error_log", currentLog + "Diferença detectada entre users_corrected e users_txt_corrected após correção!\n");
    }
    setLocal("error_log", currentLog + "Validação e correção concluídas. Valores salvos em users_corrected e users_txt_corrected.\n");
    return usersResult.valid && usersTxtResult.valid;
}

try {
    let success = processVariables();
    if (!success) {
        let currentLog = local("error_log") || "";
        setLocal("error_log", currentLog + "Erro durante a validação/correção.\n");
    }
} catch (e) {
    let currentLog = local("error_log") || "";
    setLocal("error_log", currentLog + `Erro geral: ${e.message}\n`);
    setLocal("err_local", "err_var_users");
}

var portasDisponiveis = "7774,7775,7776,7777,7778,7779,7780,7781,7782,7783,7784,7785,7786,7787,7788,7789,7790,7791,7792,7793,7794,7795,7796,7797,7798,7799";
var listaDeImagens = local('port_img');
var portaSelecionada = local('port');

var arrayPortas = portasDisponiveis.split(',');
var arrayImagens = listaDeImagens.split('\n');

if (arrayPortas.length !== arrayImagens.length) {
  setLocal('http_img', 'Erro: Número de portas e URLs não coincide!');
} else {
  var indice = arrayPortas.indexOf(portaSelecionada);
  
  if (indice !== -1) {
    setLocal('http_img', arrayImagens[indice]);
  } else {
    setLocal('http_img', 'Porta não encontrada!');
  }
}
