var port = local('port');
var phone = local('number');

var missingVariables = [];

if (!port) {
    missingVariables.push("| %port |");
}
if (!phone) {
    missingVariables.push("| %number |");
}

if (missingVariables.length > 0) {
    if (missingVariables.length === 1) {
        variable_status = "A variável\n" + missingVariables[0] + "\n não está definida.\n\n";
        variable_status += "The variable\n" + missingVariables[0] + "\n is not defined.";
        setLocal('variable_status', variable_status);
        setLocal('variable_err', "no variable");
    } else {
        variable_status = "As variáveis\n" + missingVariables.join(", ") + "\n não estão definidas.\n\n";
        variable_status += "The variables\n" + missingVariables.join(", ") + "\n are not defined.";
        setLocal('variable_status', variable_status);
        setLocal('variable_err', "no variable");
    }
} else {
    variable_status = "Todas as variáveis estão definidas.\n\n";
    variable_status += "All variables are defined.";
    setLocal('variable_status', variable_status);
}

