const users = %user_to_remove;

function createButtons() {
    const box = document.querySelector(".box-buttons");
    box.innerHTML = '';

    users.forEach((user) => {
        const button = document.createElement("button");
        button.classList.add("criateButtons");
        button.textContent = user.phone;

        const portNumber = document.createElement("span");
        portNumber.textContent = `Porta do Usuário: ${user.port}`;
        portNumber.classList.add("port-number");
      

        button.addEventListener("click", () => {
           
            document.querySelectorAll(".criateButtons").forEach(btn => {
                btn.classList.remove("selected");
            });
            button.classList.add("selected");
 document.querySelectorAll(".port-number").forEach(el => {
                el.style.display = "none";
            });
            portNumber.style.display = "inline";
            sessionStorage.setItem('Phone', user.phone);
            sessionStorage.setItem('Port', user.port);
          
        });

        button.appendChild(portNumber);
        box.appendChild(button);
    });
}

function showSelectedNumber() {
    const Phone = sessionStorage.getItem('Phone');
    const Port = sessionStorage.getItem('Port');
    const errorMessage = document.getElementById("inf-user-error");

    if (Phone) {
//alert(`${Port}`)
//alert(`${Phone}`)


tk.setLocal("phone", `${Phone}`);
tk.setLocal("port", `${Port}`);
tk.destroyScene("UNLINK_USER_FROM_WHATSAPP_WEB v(1.0)");


        errorMessage.style.display = "none";
    } else {
        errorMessage.textContent = "Por favor, selecione o usuário antes de continuar.";
        errorMessage.style.display = "block";
    }
}

document.addEventListener("DOMContentLoaded", () => {
    createButtons();
    const submitButton = document.querySelector(".submit-button");
    submitButton.addEventListener("click", showSelectedNumber);
});

