document.addEventListener('DOMContentLoaded', () => {
    // Função para gerar os botões de ports de forma dinâmica
    function generatePortButtons() {
        const portsContainer = document.getElementById("ports");
        for (let i = 1025; i <= 9999; i++) {
            if (i === 3129) continue; // Exclui a porta 3129
            const button = document.createElement("button");
            button.textContent = `${i}`;
            button.type = "button";
            button.className = "port-btn";
            button.onclick = function () {
                selectPort(i, button);
            };
            portsContainer.appendChild(button);
        }
    }

    // Função para selecionar a port
    function selectPort(port, button) {
        const allButtons = document.querySelectorAll("#ports button");
        allButtons.forEach(btn => btn.classList.remove("selected"));
        button.classList.add("selected");
        const selectedPort = document.getElementById("selectedPort");
        selectedPort.textContent = `Porta selecionada ${port}`;
        document.getElementById("portsError").style.display = "none";
    }

    function validatePhone(input) {
        const phonePattern = /^\d{2} \d{2} \d{8}$/;
        return phonePattern.test(input);
    }

    function formatphone(input) {
        let value = input.value.replace(/\D/g, "");
        if (value.length <= 2) {
            value = value.replace(/(\d{1,2})/, "$1");
        } else if (value.length <= 4) {
            value = value.replace(/(\d{1,2})(\d{1,2})/, "$1 $2");
        } else {
            value = value.replace(/(\d{1,2})(\d{1,2})(\d{1,8})/, "$1 $2 $3");
        }
        input.value = value;
        if (validatePhone(input.value)) {
            console.log("Número válido:", input.value);
        } else {
            console.log("Formato inválido");
        }
    }

    document.querySelectorAll(".inputTel").forEach(phone => {
        phone.addEventListener("input", function () {
            formatphone(phone);
        });
    });

    function validatePhone() {
        const phone = document.getElementById("phone").value;
        const phoneError = document.getElementById("phoneError");
        const phoneValid = document.getElementById("phoneValid");
        const phonePattern = /^\d{2}\s\d{2}\s\d{8}$/;
        if (phonePattern.test(phone)) {
            phoneValid.style.display = "block";
            phoneError.style.display = "none";
        } else {
            phoneValid.style.display = "none";
            phoneError.style.display = "block";
        }
    }

    function validateForm(event) {
        event.preventDefault();
        const selectedPort = document.getElementById("selectedPort");
        if (selectedPort.textContent === "") {
            document.getElementById("portsError").style.display = "block";
        } else {
            document.getElementById("portsError").style.display = "none";
            const phone = document.getElementById("phone").value;
            const modo_op = document.querySelector('input[name="modo_op"]:checked')
                ? document.querySelector('input[name="modo_op"]:checked').value
                : "";
            const portSelecionada = selectedPort.textContent.replace("Porta selecionada ", "");
            const formData = { phone, port: portSelecionada, modo_op };
            ///alert(JSON.stringify(formData, null, 2));
            tk.setLocal("json_conect", JSON.stringify(formData, null, 2));
            tk.destroyScene('SCENE_TO_CONNECT_WUZAPI_TO_WHATSAPP_WEB v(1.0)');
        }
    }

    function lidarEnviar() {
        const inputs = document.querySelectorAll("#enviar");
        inputs.forEach(input => input.click());
    }

    function lidarEnviarReceber() {
        const inputs = document.querySelectorAll('input[type="radio"]');
        inputs.forEach(input => (input.checked = false));
        document.getElementById("enviarReceber").checked = true;
    }

    generatePortButtons();

    document.getElementById("phone").addEventListener("input", validatePhone);
    document.getElementById("form").addEventListener("submit", validateForm);
    document.getElementById("enviar").addEventListener("click", lidarEnviar);
    document.getElementById("enviarReceber").addEventListener("click", lidarEnviarReceber);

    // Função para identificar o tipo de chave PIX
    function identifyPixType(pixValue) {
        const cpfPattern = /^\d{11}$/;
        const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        const uuidPattern = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;

        if (cpfPattern.test(pixValue)) {
            return "Chave PIX: CPF";
        } else if (emailPattern.test(pixValue)) {
            return "Chave PIX: E-mail";
        } else if (uuidPattern.test(pixValue)) {
            return "Chave PIX: Chave Aleatória";
        } else {
            return "Chave PIX: Tipo Desconhecido";
        }
    }

    // Adicionar funcionalidade aos ícones de informação
    document.querySelectorAll('.info-icon').forEach(icon => {
        icon.addEventListener('click', (e) => {
            e.preventDefault();
            e.stopPropagation();
            const mode = icon.getAttribute('data-mode');
            const modal = document.getElementById('modeModal');
            const description = document.querySelector(`#modeDescription .mode-desc[data-mode="${mode}"]`);
            document.querySelectorAll('.mode-desc').forEach(desc => desc.style.display = 'none');
            if (description) description.style.display = 'block';
            const rect = icon.getBoundingClientRect();
            modal.querySelector('.modal-content').style.top = `${rect.top + window.scrollY}px`;
            modal.querySelector('.modal-content').style.left = `${rect.right + 5}px`;
            modal.style.display = 'block';
            console.log("Modal opened for mode:", mode);
        });
    });

    // Fechar os modals ao clicar fora
    document.addEventListener('click', (e) => {
        const modeModal = document.getElementById('modeModal');
        const donateModal = document.getElementById('donateModal');
        const modeModalContent = modeModal.querySelector('.modal-content');
        const donateModalContent = donateModal.querySelector('.modal-content');
        const isInfoIconClick = e.target.classList.contains('info-icon');
        const isDonateButtonClick = e.target.classList.contains('donate-button');

        if (modeModal.style.display === 'block' && !modeModalContent.contains(e.target) && !isInfoIconClick) {
            modeModal.style.display = 'none';
        }
        if (donateModal.style.display === 'block' && !donateModalContent.contains(e.target) && !isDonateButtonClick) {
            donateModal.style.display = 'none';
        }
    });

    // Fechar os modals ao clicar no botão de fechar
    document.querySelectorAll('.close').forEach(close => {
        close.addEventListener('click', (e) => {
            e.stopPropagation();
            close.parentElement.parentElement.style.display = 'none';
        });
    });

    // Função para abrir o modal de doação
    function openDonateModal() {
        const modal = document.getElementById('donateModal');
        const buttonRect = document.querySelector('.donate-button').getBoundingClientRect();
        const pixValue = document.getElementById('pixInput').value;
        const pixType = document.getElementById('pixType');
        pixType.textContent = identifyPixType(pixValue);
        modal.querySelector('.modal-content').style.top = `${buttonRect.bottom + window.scrollY + 5}px`;
        modal.querySelector('.modal-content').style.right = `${window.innerWidth - buttonRect.right}px`;
        modal.querySelector('.modal-content').style.left = 'auto';
        modal.style.display = 'block';
    }

    // Adicionar funcionalidade ao botão Doar
    document.querySelector('.donate-button').addEventListener('click', (e) => {
        e.stopPropagation();
        openDonateModal();
    });

    // Copiar PIX e fechar modal
    document.querySelector('.copy-button').addEventListener('click', (e) => {
        e.stopPropagation();
        const pixInput = document.getElementById('pixInput');
        pixInput.select();
        document.execCommand('copy');
        ///alert('PIX copiado para a área de transferência!');
        document.getElementById('donateModal').style.display = 'none';
    });

    // Efeito de tremer no botão Doar a cada 5 segundos
    const donateButton = document.querySelector('.donate-button');

    function shakeDonateButton() {
        donateButton.classList.remove('shake');
        void donateButton.offsetWidth;
        donateButton.classList.add('shake');
    }

    shakeDonateButton();
    setInterval(shakeDonateButton, 5000);

    // Função para centralizar a porta pesquisada e verificar porta inválida
    const portSearch = document.getElementById('portSearch');
    const portSearchError = document.getElementById('portSearchError');
    portSearch.addEventListener('input', () => {
        let searchValue = portSearch.value.trim();
        
        // Limitar a 4 dígitos
        if (searchValue.length > 4) {
            portSearch.value = searchValue.slice(0, 4);
            searchValue = portSearch.value;
        }

        // Verificar se a porta é válida quando tiver 4 dígitos
        if (searchValue.length === 4) {
            const portNumber = parseInt(searchValue, 10);
            // Verificar se a porta está fora do intervalo 1025-9999 ou é 3129
            if (portNumber < 1025 || portNumber > 9999 || portNumber === 3129) {
                portSearchError.textContent = `A porta ${searchValue} não pode ser utilizada.`;
                portSearchError.style.display = 'block';
                portSearch.value = ''; // Limpar o input após exibir o erro
            } else {
                portSearchError.style.display = 'none';
                // Continuar com a lógica de centralização
                const portButtons = document.querySelectorAll('#ports button');
                let targetButton = null;
                portButtons.forEach(button => {
                    if (button.textContent === searchValue) {
                        targetButton = button;
                    }
                });
                if (targetButton) {
                    targetButton.scrollIntoView({ block: 'center', behavior: 'smooth' });
                }
                portSearch.value = ''; // Limpar o input após a pesquisa
            }
        } else {
            portSearchError.style.display = 'none';
        }
    });

    // Abrir o modal de doação automaticamente ao carregar a página
    openDonateModal();
});

