const textSpans = document.querySelectorAll('.main-text span');
const progressBar = document.querySelector('.loading-bar .progress');
let isPaused = false;
let animationStartTime = null;
let animationFrame;
let isProcessing = false;

function animate() {
    if (isPaused) return;

    const elapsed = (Date.now() - animationStartTime) / 1000;
    const cycleDuration = 10;
    const progress = (elapsed % cycleDuration) / cycleDuration;

    progressBar.style.width = `${progress * 100}%`;

    const totalLetters = textSpans.length;
    const letterInterval = cycleDuration / totalLetters;
    textSpans.forEach((span, index) => {
        const letterTime = index * letterInterval;
        if (elapsed % cycleDuration >= letterTime) {
            span.classList.add('visible');
        } else {
            span.classList.remove('visible');
        }
    });

    animationFrame = requestAnimationFrame(animate);
}

function startAnimation() {
    animationStartTime = Date.now();
    if (!isPaused) {
        animationFrame = requestAnimationFrame(animate);
    }
}

function togglePause() {
    isPaused = !isPaused;
    if (isPaused) {
        cancelAnimationFrame(animationFrame);
    } else {
        animationStartTime = Date.now() - (animationStartTime ? (Date.now() - animationStartTime) % 10000 : 0);
        animate();
    }
}

startAnimation();

document.querySelector('.main-text').addEventListener('touchstart', (e) => {
    e.preventDefault();
    togglePause();
});
document.querySelector('.main-text').addEventListener('click', togglePause);

const updateNowBtn = document.getElementById('update-now-btn');
const scheduleBtn = document.getElementById('schedule-btn');
const scheduleModal = document.getElementById('schedule-modal');
const confirmationModal = document.getElementById('confirmation-modal');
const errorModal = document.getElementById('error-modal');
const confirmScheduleBtn = document.getElementById('confirm-schedule-btn');
const backScheduleIcon = document.getElementById('back-schedule-icon');
const backConfirmationIcon = document.getElementById('back-confirmation-icon');
const confirmBtn = document.getElementById('confirm-btn');
const scheduleTimeInput = document.getElementById('schedule-time');
const confirmationText = document.getElementById('confirmation-text');
const errorText = document.getElementById('error-text');

function closeAllModals() {
    scheduleModal.style.display = 'none';
    confirmationModal.style.display = 'none';
    errorModal.style.display = 'none';
}

function openModal(modal) {
    closeAllModals();
    modal.style.display = 'flex';
    isProcessing = false;
}

function closeModal(modal) {
    modal.style.display = 'none';
    isProcessing = false;
}

function getCurrentBrazilianTime() {
    const now = new Date();
    return now.toLocaleTimeString('pt-BR', { timeZone: 'America/Sao_Paulo', hour: '2-digit', minute: '2-digit', hour12: false });
}

function getCurrentBrazilianTimePlusFiveMinutes() {
    const now = new Date();
    now.setMinutes(now.getMinutes() + 5); // Adiciona 5 minutos
    return now.toLocaleTimeString('pt-BR', { timeZone: 'America/Sao_Paulo', hour: '2-digit', minute: '2-digit', hour12: false });
}

function isTimeValid(selectedTime, currentTime) {
    const [selectedHours, selectedMinutes] = selectedTime.split(':').map(Number);
    const [currentHours, currentMinutes] = currentTime.split(':').map(Number);
    const selectedTotalMinutes = selectedHours * 60 + selectedMinutes;
    const currentTotalMinutes = currentHours * 60 + currentMinutes;
    const minAllowedMinutes = currentTotalMinutes + 5; // Deve ser pelo menos 5 minutos à frente
    return selectedTotalMinutes >= minAllowedMinutes;
}

function formatBrazilianTime(time) {
    if (!time) return '';
    const [hours, minutes] = time.split(':');
    return `${hours}:${minutes}`;
}

function showErrorModal(message) {
    errorText.textContent = message;
    openModal(errorModal);
    setTimeout(() => {
        closeModal(errorModal);
        isProcessing = false; // Reset isProcessing before reopening schedule modal
        openModal(scheduleModal);
    }, 5000);
}

errorModal.addEventListener('click', (e) => {
    if (e.target === errorModal) {
        closeModal(errorModal);
        openModal(scheduleModal);
    }
});

updateNowBtn.addEventListener('click', () => {
    ///alert("atualizar agora");

    tk.setLocal("to_update","update now");

     tk.destroyScene('UPDATES v(1.0)');
});

scheduleBtn.addEventListener('click', () => {
    if (isProcessing) return;
    isProcessing = true;
    const timePlusFiveMinutes = getCurrentBrazilianTimePlusFiveMinutes();
    scheduleTimeInput.value = timePlusFiveMinutes;
    openModal(scheduleModal);
});

backScheduleIcon.addEventListener('click', () => {
    if (isProcessing) return;
    isProcessing = true;
    closeModal(scheduleModal);
});

confirmScheduleBtn.addEventListener('click', () => {
    if (isProcessing) return;
    isProcessing = true;
    const selectedTime = scheduleTimeInput.value;
    const currentTime = getCurrentBrazilianTime();
    if (selectedTime) {
        if (isTimeValid(selectedTime, currentTime)) {
            confirmationText.textContent = `Confirmar agendamento para ${formatBrazilianTime(selectedTime)} ?`;
            closeModal(scheduleModal);
            openModal(confirmationModal);
        } else {
            showErrorModal(`Horário inválido! Escolha um horário pelo menos 5 minutos do horário atual ${currentTime}.`);
        }
    } else {
        showErrorModal('Por favor, selecione seu horário.');
    }
});

backConfirmationIcon.addEventListener('click', () => {
    if (isProcessing) return;
    isProcessing = true;
    closeModal(confirmationModal);
    openModal(scheduleModal);
});

confirmBtn.addEventListener('click', () => {
    if (isProcessing) return;
    isProcessing = true;
    const selectedTime = formatBrazilianTime(scheduleTimeInput.value);
    closeModal(confirmationModal);
    ///alert(JSON.stringify({ horario: selectedTime }));

    tk.setLocal("active_scheduling",JSON.stringify({ horario: selectedTime }));

    tk.destroyScene('UPDATES v(1.0)');
});

