const textSpans = document.querySelectorAll('.main-text span');
const progressBar = document.querySelector('.loading-bar .progress');
let isPaused = false;
let animationStartTime = null;
let animationFrame;
let isProcessing = false;

// Função para corrigir codificação incorreta (ex.: ISO-8859-1 para UTF-8)
function fixEncoding(str) {
    try {
        // Converte texto potencialmente corrompido de ISO-8859-1 para UTF-8
        return decodeURIComponent(escape(str));
    } catch (e) {
        // Se falhar, retorna o texto original
        return str;
    }
}

const infoText = `%update_info`;

function animate() {
    if (isPaused) return;

    const elapsed = (Date.now() - animationStartTime) / 1000;
    const cycleDuration = 10;
    const progress = (elapsed % cycleDuration) / cycleDuration;

    progressBar.style.width = `${progress * 100}%`;

    const totalLetters = textSpans.length;
    const letterInterval = cycleDuration / totalLetters;
    textSpans.forEach((span, index) => {
        const letterTime = index * letterInterval;
        if (elapsed % cycleDuration >= letterTime) {
            span.classList.add('visible');
        } else {
            span.classList.remove('visible');
        }
    });

    animationFrame = requestAnimationFrame(animate);
}

function startAnimation() {
    animationStartTime = Date.now();
    if (!isPaused) {
        animationFrame = requestAnimationFrame(animate);
    }
}

function togglePause() {
    isPaused = !isPaused;
    if (isPaused) {
        cancelAnimationFrame(animationFrame);
    } else {
        animationStartTime = Date.now() - (animationStartTime ? (Date.now() - animationStartTime) % 10000 : 0);
        animate();
    }
}

startAnimation();

document.querySelector('.main-text').addEventListener('touchstart', (e) => {
    e.preventDefault();
    togglePause();
});
document.querySelector('.main-text').addEventListener('click', togglePause);

const exitBtn = document.getElementById('exit-btn');
const viewBtn = document.getElementById('view-btn');
const modal = document.getElementById('info-modal');
const modalText = document.getElementById('modal-text');

exitBtn.addEventListener('click', () => {
    ///alert('Encerrando sessão...');
    tk.setLocal("exit","exit");
    tk.destroyScene("NO_UPDATES v(1.0)");
});

viewBtn.addEventListener('click', () => {
    // Corrigir codificação, preservando quebras de linha e bullet points originais
    const cleanInfoText = fixEncoding(infoText).trim(); // Remove apenas espaços extras no início e fim
    modalText.innerText = cleanInfoText; // Usa innerText para preservar quebras de linha
    modal.style.display = 'flex';
});

modal.addEventListener('click', (e) => {
    if (e.target === modal) {
        modal.classList.add('closing');
        modal.querySelector('.modal-content').classList.add('closing');
        setTimeout(() => {
            modal.style.display = 'none';
            modal.classList.remove('closing');
            modal.querySelector('.modal-content').classList.remove('closing');
        }, 500); // Matches the 0.5-second animation duration
    }
});

var intentData = local("shortcut");

// Extrair JID (funciona para contato ou grupo)
var jidStart = intentData.indexOf("S.jid=");
var numero = "Não encontrado";

if (jidStart >= 0) {
    jidStart += 6; // tamanho de "S.jid="
    var jidEnd = intentData.indexOf(";", jidStart); // termina no ;
    if (jidEnd >= 0) {
        var jid = intentData.substring(jidStart, jidEnd);
        numero = jid.split("%40")[0]; // remove o sufixo (ex: %40s.whatsapp.net ou %40g.us)
    }
}

// Extrair nome (displayname)
var nameStart = intentData.indexOf("S.displayname=");
var nome = "Não encontrado";

if (nameStart >= 0) {
    nameStart += 14; // tamanho de "S.displayname="
    var nameEnd = intentData.indexOf(";", nameStart);
    if (nameEnd >= 0) {
        var nomeCodificado = intentData.substring(nameStart, nameEnd);
        nome = decodeURIComponent(nomeCodificado);
    }
}

