setLocal("number", numero);
setLocal("name", nome);

var phone = local("phone"); // Variável de entrada %phone
var countryCode = local("cod"); // Variável de entrada countryCode

// Função para formatar corretamente o número de telefone
function formatPhoneNumber(phoneNumber, countryCode) {
    phoneNumber = phoneNumber.replace(/\D/g, ""); // Remove caracteres não numéricos

    if (phoneNumber.length === 10) { // Exemplo: 8197573129
        return `+${countryCode} ${phoneNumber.substring(0, 2)} ${phoneNumber.substring(2, 6)}-${phoneNumber.substring(6)}`;
    } else if (phoneNumber.length === 11) { // Exemplo: 81999412266
        return `+${countryCode} ${phoneNumber.substring(0, 2)} ${phoneNumber.substring(2, 7)}-${phoneNumber.substring(7)}`;
    } else {
        return null;  // Número inválido
    }
}

// Verifica se o código do país foi informado corretamente
if (!countryCode || countryCode.trim().length < 2) {
    setLocal("erro_phone", "Phone_Invalid");  // Define erro se o código do país for inválido
} else {
    var formattedPhone = formatPhoneNumber(phone, countryCode);  // Formatar número

    // Se o número for válido, definir na variável %formatted_phone
    if (formattedPhone) {
        setLocal("formatted_phone", formattedPhone);  // Define o número formatado
        setLocal("err_phone", "Phone_Valid");  // Indica que o telefone é válido
    } else {
        setLocal("err_phone", "Phone_Invalid");  // Define erro se o número for inválido
        setLocal("formatted_phone", "");  // Limpa a variável formatted_phone se o número for inválido
    }
}

