var phoneNumbers = local("phone_numbers"); // Recebe a variável de entrada com números separados por vírgula

function validatePhoneNumbers(numbers) {
  // Divide os números da string em um array e remove espaços extras
  var phoneArray = numbers.split(",").map(function(num) {
    return num.trim(); // Remove espaços antes e depois de cada número
  });
  
  var validNumbers = [];
  var errors = [];

  // Valida cada número
  phoneArray.forEach(function(num, index) {
    if (num.length !== 12) {
      errors.push(`Número incorreto: "${num}" (Índice ${index + 1}) - O número precisa ter 12 dígitos.`);
    } else if (/\s/.test(num)) {
      errors.push(`Número incorreto: "${num}" (Índice ${index + 1}) - O número não pode ter espaços.`);
    } else {
      validNumbers.push(num);
    }
  });

  // Se houver erros, retorna o objeto com erros
  if (errors.length > 0) {
    return {
      "Errors": errors
    };
  }

  // Se não houver erros, cria o JSON com os números válidos
  var result = {
    "Phone": validNumbers
  };

  return result;
}

var formattedData = validatePhoneNumbers(phoneNumbers);

// Formata o JSON com indentação
var formattedJSON = JSON.stringify(formattedData, null, 2);

setLocal("formatted_phone", formattedJSON);

